mapper.select:
		SELECT * FROM ACCOUNT 
		WHERE 
    	ID = #{id} AND PWD = #{pwd}

		SELECT NO FROM ACCOUNT 
		WHERE 
    	ID = #{id}

SELECT ID FROM ACCOUNT 
WHERE ID = #{id}
ORDER BY NAME

SELECT ID FROM ACCOUNT 
WHERE EMAIL = #{email}
ORDER BY NAME

mapper.update:
UPDATE ACCOUNT
SET PWD = #{pwd}
WHERE ID = #{id}
