mapper.select:
    
	    SELECT Q.*, A.ID, A.CRITIC_CHECK
	    FROM (
	        SELECT ROWNUM R_NUM, Q.*
	        FROM (
	            SELECT *
	            FROM QA
	            ORDER BY QA_NO DESC
	        ) Q
	        WHERE ROWNUM <= #{end}
	    ) Q
	    INNER JOIN ACCOUNT A ON Q.NO = A.NO
	    WHERE R_NUM >= #{begin}
	    ORDER BY R_NUM ASC

SELECT * FROM QA
WHERE 1 = 2
WHERE QA_CONTENT LIKE '%' || #{keyword} || '%'
WHERE TO_CHAR(QA_DATE, 'YYYYMMDD') = #{keyword}
WHERE QA_CATEGORY = #{keyword}
WHERE QA_CATEGORY = #{qaCategory} AND QA_CONTENT LIKE '%' || #{keyword} || '%'
WHERE QA_CATEGORY = #{keyword}
WHERE QA_CATEGORY = #{qaCategory} AND QA_CONTENT LIKE '%' || #{keyword} || '%'
AND 1 = 2
ORDER BY QA_NO DESC

mapper.update:
UPDATE QA
	SET QA_CONTENT = #{qaContent}
		, QA_DATE = SYSDATE
	WHERE QA_NO = #{qaNo}
