mapper.select:
SELECT M.*, R.* 
FROM REVIEW R, MOVIE M
WHERE R.MV_NO = M.MV_NO

	
		AND M.MV_TITLE = #{keyword}
	
	
		AND R.RV_NICK = #{keyword}
	
	
		AND TO_CHAR(R.RV_DATE, 'YYYYMMDD') = #{keyword}
	
	
		AND 1 = 2
	

ORDER BY R.RV_NO DESC

SELECT R.*
FROM REVIEW R, MOVIE M
WHERE R.MV_NO = M.MV_NO
AND R.MV_NO = #{mvNO}
AND R.RV_NO = #{rvNo}
ORDER BY R.RV_NO DESC


	SELECT *
	FROM (SELECT ROWNUM R_NUM, R.*
	      FROM (SELECT *
	            FROM REVIEW
	            ORDER BY RV_NO DESC
	            ) R
	      WHERE ROWNUM <= #{end}
	      )
	WHERE R_NUM >= #{begin}

mapper.update:
UPDATE REVIEW
SET RV_NICK = #{nick}
WHERE NO = #{no}
