mapper.select:
SELECT COUNT(*) AS SUM
FROM WARN
WHERE RV_NO = #{rvNo}

SELECT COUNT(*) AS SUM
FROM WARN
WHERE NO = #{no}

SELECT COUNT(*) AS SUM
FROM WARN
WHERE PS_NO = #{psNo}

SELECT COUNT(*) AS CNT
FROM WARN
WHERE NO = #{no}
AND RV_NO = #{rvNo}

mapper.delete:
DELETE FROM WARN
WHERE NO = #{no}
AND RV_NO = #{rvNo}
